package logicalPrograms;

public class Logical_swap2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a=10,b=20;
		 
		 System.out.println("before a:"+a+" b:"+b);
		 
		 a=a+b;
		 b=a-b;
		 a=a-b;System.out.println("after a:"+a+" b:"+b);

	}

}
